import { useState } from "react";
import "./dropdown_menu.css";

// title 일치 개수 확인
const countTitleOccurrences = (items) => {
  const titleCount = items.reduce((acc, item) => {
    acc[item.title] = (acc[item.title] || 0) + 1;
    return acc;
  }, {});
  return titleCount;
};

// 기능별 정렬
const getSortedItems = (items, order) => {
  const titleCount = countTitleOccurrences(items);

  return [...items].sort((a, b) => {
    if (order === "createdAt") {
      return new Date(b.createdAt) - new Date(a.createdAt);
    } else if (order === "badges") {
      return b.badges - a.badges;
    } else if (order === "titleCount") {
      return (titleCount[b.title] || 0) - (titleCount[a.title] || 0);
    } else if (order === "achievedBadges") {
      return b.achievedBadges - a.achievedBadges;
    } else if (order === "likes") {
      return b.likes - a.likes;
    }
    return 0;
  });
};

function Dropdown({ options, items, onSelect }) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(options[0]);
  const [sortedItems, setSortedItems] = useState(
    getSortedItems(items, options[0])
  );

  const handleToggle = () => setIsOpen(!isOpen);

  const handleSelect = (option) => {
    setSelectedOption(option);
    const newSortedItems = getSortedItems(items, option);
    setSortedItems(newSortedItems);
    onSelect(option);
    setIsOpen(false);
  };

  return (
    <div className="dropdown">
      <button onClick={handleToggle}>
        {selectedOption}
        <span className="arrow">{isOpen ? "▲" : "▼"}</span>
      </button>
      {isOpen && (
        <ul className="dropdown-menu">
          {options.map((option) => (
            <li key={option} onClick={() => handleSelect(option)}>
              {option}
            </li>
          ))}
        </ul>
      )}
      {/* 정렬된 아이템을 표시하거나 반환 */}
      <CardGroup items={sortedItems} />
    </div>
  );
}

export default Dropdown;
