import { useState, useMemo } from "react";
import CardGroup from "./card_group.js";
import items from "../mock.json";
import Dropdown from "./dropdown_menu.js";
import Public from "./Public.js";

// title 일치 개수 확인
const countTitleOccurrences = (items) => {
  const titleCount = items.reduce((acc, item) => {
    acc[item.title] = (acc[item.title] || 0) + 1;
    return acc;
  }, {});
  return titleCount;
};

// 기능별 정렬
const getSortedItems = (items, order) => {
  const titleCount = countTitleOccurrences(items);

  return [...items].sort((a, b) => {
    if (order === "createdAt") {
      return new Date(b.createdAt) - new Date(a.createdAt);
    } else if (order === "badges") {
      return b.badges - a.badges;
    } else if (order === "titleCount") {
      return (titleCount[b.title] || 0) - (titleCount[a.title] || 0);
    } else if (order === "achievedBadges") {
      return b.achievedBadges - a.achievedBadges;
    } else if (order === "likes") {
      return b.likes - a.likes;
    }
    return 0;
  });
};

function App() {
  const [order, setOrder] = useState("createdAt");

  const sortedItems = useMemo(() => getSortedItems(items, order), [order]);

  const handleSelect = (option) => {
    if (option === "최신순") setOrder("createdAt");
    else if (option === "게시글 많은순") setOrder("titleCount");
    else if (option === "공감순") setOrder("likes");
    else if (option === "획득 배지순") setOrder("achievedBadges");
  };

  /* api 연결
  const handleLoad = async () => {
    const { memories } = await getMemories();
    setItems(memories);
  };

  useEffect(() => {
    handleLoad();
  }, []);
*/
  return (
    <div>
      <Public />
      <Dropdown
        options={["최신순", "게시글 많은순", "공감순", "획득 배지순"]}
        onSelect={handleSelect}
      />
      <CardGroup items={sortedItems} />
    </div>
  );
}

export default App;
