//공개, 비공개 탭 기능
import React, { useState } from "react";
import CardGroup from "./card_group";

function Public({ items, onFilter }) {
  const [filterOption, setFilterOption] = useState("공개"); // 기본값: "공개"

  const filteredItems = items.filter((item) => item.option === filterOption);

  const handleFilterClick = (option) => {
    setFilterOption(option);
    onFilter(filteredItems);
  };

  return (
    <div>
      <div>
        <button onClick={() => handleFilterClick("공개")}>공개</button>
        <button onClick={() => handleFilterClick("비공개")}>비공개</button>
      </div>
      <CardGroup items={filteredItems} /> {/* 필터링된 아이템만 전달 */}
    </div>
  );
}

export default Public;
