// 공개 그룹 목록 페이지 - card/group 컴포넌트
import React from "react";
import "./card_group.css";

// 시간계산 함수
function timeAgo(createdAt) {
  const now = new Date();
  const createdDate = new Date(createdAt);
  const difference = now - createdDate;

  const seconds = Math.floor(difference / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);
  const days = Math.floor(hours / 24);
  const months = Math.floor(days / 30);
  const years = Math.floor(months / 12);

  if (years > 0) {
    return `${years}년 전`;
  } else if (months > 0) {
    return `${months}달 전`;
  } else if (days > 0) {
    return `${days}일 전`;
  } else if (hours > 0) {
    return `${hours}시간 전`;
  } else if (minutes > 0) {
    return `${minutes}분 전`;
  } else {
    return `방금 전`;
  }
}

function CardGroupItem({ item }) {
  const { img, createdAt, option, title, content, badges, memories, like } =
    item;

  return (
    <div className="CardGroupItem">
      {img && <img src={img} alt={title} />}
      <div>{timeAgo(createdAt)}</div>
      <div>{option}</div>
      <div>{title}</div>
      <div>{content}</div>
      <div>획득배지{badges}</div>
      <div>추억{memories}</div>
      <div>그룹 공감{like}</div>
    </div>
  );
}

function CardGroup({ items }) {
  return (
    <ul className="CardGroup">
      {items.map((item) => (
        <li key={item.id}>
          <CardGroupItem item={item} />
        </li>
      ))}
    </ul>
  );
}

export default CardGroup;
