import { useState, useMemo } from "react";
import items from "../mock.json";
import Dropdown from "./dropdown_menu.js";
import CardGroup from "./card_group.js";
import Public from "./Public.js";

function App() {
  const [order, setOrder] = useState("createdAt");

  const handleSelect = (option) => {
    if (option === "최신순") setOrder("createdAt");
    else if (option === "게시글 많은순") setOrder("titleCount");
    else if (option === "공감순") setOrder("likes");
    else if (option === "획득 배지순") setOrder("achievedBadges");
  };

  /* api 연결
  const handleLoad = async () => {
    const { memories } = await getMemories();
    setItems(memories);
  };

  useEffect(() => {
    handleLoad();
  }, []);
*/
  return (
    <div>
      <Public />
      <Dropdown
        options={["최신순", "게시글 많은순", "공감순", "획득 배지순"]}
        onSelect={handleSelect}
      />
      {/* <CardGroup items={sortedItems} /> */}
    </div>
  );
}

export default App;
